<template>  <div class="form-wrapper">    <form @submit.prevent="validate()">      <span class="close" @click="$emit('close-modal')">&times;</span>      <div v-if="errors.length">        <ul>          <li v-for="error in errors" :key="error" style="color: red">{{error}}</li>        </ul>      </div>      <div>        <label for="name">Name</label>        <input          v-model="event.name"          id="name"          type="text"        >      </div>      <div>        <label for="details">Details</label>        <input          v-model="event.details"          id="details"          type="text"        >      </div>      <div>        <label for="date">Date</label>        <input          v-model="event.date"          id="date"          type="date"        >      </div>      <div>        <label for="background">Color</label>        <input          v-model="event.background"          id="background"          type="color"        >      </div>      <div>        <button type="submit">Add</button><!--      or--><!--      <button @click.prevent="$emit('add-new-event', event), $emit('close-modal')">Add</button>-->      </div>    </form>  </div></template><script>  export default {    emits: ['close-modal', 'add-new-event'],    data: () => ({      event: {        name: '',        details: '',        date: '',        background: '',      },      errors: []    }),    methods: {      submitHandler() {        this.$emit('add-new-event', this.event);      },      validate() {        this.errors = [];        for (let key in this.event) {          if (!this.event[key]) this.errors.push(`${key} is required`);        }        if (this.errors.length === 0) {          this.submitHandler();        }      }    }  }</script><style scoped>  .close {    align-self: flex-end;    cursor: pointer;  }  .form-wrapper {    background: rgba(0, 0, 0, .75);    position: absolute;    top: 0;    right: 0;    bottom: 0;    left: 0;    display: flex;    justify-content: center;    align-items: center;  }  form {    display: flex;    flex-direction: column;    background: white;    min-width: 60vw;    min-height: 40vh;    padding: 2rem;    border-radius: 0.3rem;  }  form > div {    display: flex;    flex-direction: column;    margin: 1rem;    font-size: 1.6rem;  }  form input {    margin: 0.6rem 0;    padding: 0.6rem 1rem;    border: 1px lightgray solid;    border-radius: 0.3rem;  }  form button {    background: greenyellow;    width: 100px;    border: none;    padding: 0.6rem;    border-radius: 0.3rem;    font-size: 16px;  }  label {    color: #530c0c;  }</style>